/**
 * Adapter command implementations
 * @fileoverview Commands for managing and inspecting adapters
 */

import { getAdapterInfo, listAdapters, listAdaptersWithInfo } from '../../core/index.mjs';

/**
 * List command - List all available adapters
 * @param {Object} options - Command options
 */
export async function list(options) {
  try {
    const adapters = listAdapters();
    console.log('üì¶ Available adapters:');
    for (const adapter of adapters) {
      console.log(`  ‚Ä¢ ${adapter}`);
    }
  } catch (error) {
    console.error(`‚ùå List failed: ${error.message}`);
    throw error;
  }
}

/**
 * Info command - Show detailed information about adapters
 * @param {Object} options - Command options
 */
export async function info(options) {
  try {
    const { name } = options;

    if (name) {
      // Show info for specific adapter
      const info = getAdapterInfo(name);
      if (!info) {
        throw new Error(`Adapter '${name}' not found`);
      }

      console.log(`üìã Adapter: ${info.name}`);
      console.log(`  Version: ${info.version}`);
      console.log(`  Streaming: ${info.supportsStreaming ? '‚úÖ' : '‚ùå'}`);
      console.log(`  AI-powered: ${info.isAI ? '‚úÖ' : '‚ùå'}`);
      console.log(`  Parse: ${info.hasParse ? '‚úÖ' : '‚ùå'}`);
      console.log(`  Format: ${info.hasFormat ? '‚úÖ' : '‚ùå'}`);
    } else {
      // Show info for all adapters
      const adapters = listAdaptersWithInfo();
      console.log('üìã Adapter Information:');
      for (const adapter of adapters) {
        console.log(`\n  ${adapter.name}:`);
        console.log(`    Version: ${adapter.version}`);
        console.log(`    Streaming: ${adapter.supportsStreaming ? '‚úÖ' : '‚ùå'}`);
        console.log(`    AI-powered: ${adapter.isAI ? '‚úÖ' : '‚ùå'}`);
        console.log(`    Parse: ${adapter.hasParse ? '‚úÖ' : '‚ùå'}`);
        console.log(`    Format: ${adapter.hasFormat ? '‚úÖ' : '‚ùå'}`);
      }
    }
  } catch (error) {
    console.error(`‚ùå Info failed: ${error.message}`);
    throw error;
  }
}
